class App extends Component {
  state = {
    modal: false,
    isOpen: false,
    activeItem: "1"
  }
  toggleBook = () => {
    this.setState({
      modal1: !this.state.modal1
    });
  }
  toggleMod = () => {
    this.setState({
      modal: !this.state.modal
    });
  }
  toggleCollapse = () => {
    this.setState({ isOpen: !this.state.isOpen });
  }

  toggle = tab => () => {
    if (this.state.activeItem !== tab) {
    this.setState({
      activeItem: tab
    });
    }
    
  }
